import React, { useState } from 'react';
import { getAuth, getUserByEmail } from 'firebase/auth';
import { auth } from '../firebase/Firebase';

import './Styles.css';

function RenewPassword() {
  const [email, setEmail] = useState('');
  const [error, setError] = useState(null);

  const checkEmailExists = async () => {
    try {
      const userRecord = await getUserByEmail(auth, email);

      if (userRecord) {
        // User exists, log the UID
        console.log('User UID:', userRecord.uid);
      } else {
        console.log('User with email does not exist.');
      }
    } catch (error) {
      console.error('Error checking user by email:', error.message);
    }
  };

  return (
    <div>
      <h2>Email Check</h2>
      {/* Add your email input and button here */}
      <input
        type="email"
        placeholder="Enter email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <button onClick={checkEmailExists}>Check Email</button>
    </div>
  );
}

export default RenewPassword;
