import React, { useState } from 'react'
import { getAuth  } from 'firebase/auth';
import { sendPasswordResetEmail,fetchSignInMethodsForEmail } from 'firebase/auth';
import { auth } from '../firebase/Firebase';
import './Styles.css'
function RenewPassword() {
    const [email, setEmail] = useState('');
  const [resetSent, setResetSent] = useState(false);
  const [emailExists, setEmailExists] = useState(true);

  const handleResetPassword =  async (e) => {
    e.preventDefault();
    try {
        const userRecords = await listUsers(auth);
        const emails = userRecords.map(user => user.email);

    console.log('E-mails récupérés :', emails);
    return emails;
        console.log(email); // Check the value of email
        const signInMethods = await fetchSignInMethodsForEmail(auth, email);
  console.log(signInMethods);
        // Si l'email existe
        if (signInMethods.length > 0) {
          await sendPasswordResetEmail(auth, email);
          setResetSent(true);
        } else {
          setEmailExists(false);
        }
      } catch (error) {
        console.error('Erreur lors de la vérification de l\'existence de l\'email', error.message);
      }
}
  return (
    <>
    
          <div  class="fory">
    < div class="wrappery">
    <form>
      <h2>Forgot Password</h2>
      
      <div class="input-box">
        <input  type="email" value={email} placeholder="enter your email"  onChange={(e) => setEmail(e.target.value)}   required/>
 
      </div>
      <div class="input-box">
      <button  class="btn" onClick={(e)=>handleResetPassword(e)}>Reset Password</button>
        
      </div>
       
       <div class="reset">
              {resetSent && <p>Email de réinitialisation du mot de passe envoyé. Vérifiez votre boîte de réception.</p>}
              {!emailExists && <p>Email non trouvé. Veuillez vérifier l'adresse e-mail et réessayer.</p>}
            </div>
       
      </form>
      </div>
      </div>
      
      
      </>
  )
}

export default RenewPassword
