import React, { useEffect, useState } from 'react';
import { getAuth, onAuthStateChanged, fetchSignInMethodsForEmail, sendPasswordResetEmail } from 'firebase/auth';
import { auth } from '../firebase/Firebase';
import './Styles.css';

function RenewPassword() {
  const [email, setEmail] = useState('');
  const [resetSent, setResetSent] = useState(false);
  const [emailExists, setEmailExists] = useState(true);

  const handleResetPassword = async (e) => {
    e.preventDefault();

    try {
      const auth = getAuth();

      // Check if the email is empty
      if (!email) {
        console.error('Please provide an email');
        return;
      }
      const user = auth.cu;
      console.log('Current user email:', user ? user.email : 'No user signed in');
      // Check if the email exists
      const signInMethods = await fetchSignInMethodsForEmail(auth, email);

      if (signInMethods.length > 0) {
        // If the email exists, send a password reset email
        await sendPasswordResetEmail(auth, email);
        setResetSent(true);
      } else {
        // If the email doesn't exist, set a state variable indicating it
        setEmailExists(false);
      }
    } catch (error) {
      console.error('Error during password reset:', error.message);
    }
  };

  return (
    <>
      <div className="fory">
        <div className="wrappery">
          <form>
            <h2>Forgot Password</h2>

            <div className="input-box">
              <input type="email" value={email} placeholder="enter your email" onChange={(e) => setEmail(e.target.value)} required />
            </div>
            <div className="input-box">
              <button className="btn" onClick={(e) => handleResetPassword(e)}>
                Reset Password
              </button>
            </div>

            <div className="reset">
              {resetSent && <p>Email de réinitialisation du mot de passe envoyé. Vérifiez votre boîte de réception.</p>}
              {!emailExists && <p>Email non trouvé. Veuillez vérifier l'adresse e-mail et réessayer.</p>}
            </div>
          </form>
        </div>
      </div>
    </>
  );
}

export default RenewPassword;
