import {React,useEffect,useState,useContext,useR}  from 'react'
import './Style.css'
import Conversation from '../conversation/Conversation';
import { useNavigate } from 'react-router-dom';
import { ThemeContext } from '../themeContext/ThemeContext';
import { db,onValue,ref} from '../firebase/Firebase';
function Conversations(props) {
  
  const { amiss,nomchat,prenomchat,idchat,imagechat,userid, usernom,userprenom,photo } = useContext(ThemeContext);
  const [conversation,setconvertation]=useState([]);

const [users,setusers]=useState([]);

const initialized = React.useRef(false);
  const navigate = useNavigate();
  
  

  useEffect(() => {
    if (!initialized.current) {
      initialized.current = true
      getdata();
    

}

   props.yeslog();
    const Userid =  localStorage.getItem('userid') ;
    if (Userid==null){
      navigate("/")
    }
  }, []);
   
  
  const getdata=async()=>{
    
    console.log("start")
    
        const user = localStorage.getItem('userid');
      var conver=[]
      if(user!=null){
         
        const query = ref(db, `users`);
      
      
        var arrayi=[]
      
      
      
      
      
        return await onValue(query, (snapshot) => {
         
        
      
          const data = snapshot.val();
        
       var tabinvit=[]
       var tabamis=[]
      
      
      
      
        Object.values(data).map((x)=>{
      
    setusers([...users,x])
    
            
          if(x.id==user){
    x.discussion.map((y,key)=>{
    
    if(key>0  ){
    conver.push(y);
    setconvertation([...conversation,y])
    //
    }
    })
    
    console.log(conver);
    
    }}
    
    )})
        
        }}
    
    
    
    
    const ouvreconversation=(obj)=>{
     nomchat(obj.nom)
      prenomchat(obj.photo)
       idchat(obj.id)
      prenomchat(obj.prenom)
    
      
    
    }
    
  return   (
    <>
    <div className='grand d-flex flex-row'>
     <div className='list bg-dark'>
     {conversation.map((x)=>{return(
     <div class="cards bg-dark text-white w-100 "  style={{borderBottomWidth:1,
      borderColor:"rgb(134, 13, 126)",
     borderStyle:"solid"}} >
        
        <div>
         <img src={x.avecqui.photo}  />
        </div>
    
        <div>
         <h1>{x.avecqui.nom+' '+x.avecqui.prenom}</h1>
         <div className='d-flex flex-row'>
         <h5>{x.msg[x.msg.length-1].contenu} </h5>
        </div>
         </div>
         </div>
     )})}
     </div>
     














<div className='chat bg-danger'>
<Conversation/>
</div>









     </div>
     </>
  )
   
}

export default Conversations
