import React, { useEffect, useState } from 'react';
import { getAuth, onAuthStateChanged, fetchSignInMethodsForEmail, sendPasswordResetEmail } from 'firebase/auth';
import { auth } from '../firebase/Firebase';
import './Styles.css';

function RenewPassword() {
  const [email, setEmail] = useState('');
  const [signInMethods, setSignInMethods] = useState([]);
  const [error, setError] = useState(null);

  const checkEmailExists = async () => {
    try {
      const methods = await fetchSignInMethodsForEmail(auth, email);
      setSignInMethods(methods);
      setError(null);
    } catch (error) {
      setSignInMethods([]);
      setError(error.message);
    }
  };
  return (
  <div>
      <h2>Email Check</h2>
      <div>
        <input
          type="email"
          placeholder="Enter email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <button onClick={checkEmailExists}>Check Email</button>
      </div>
      <div>
        {signInMethods.length > 0 ? (
          <p>Email exists. Sign-in methods: {signInMethods.join(', ')}</p>
        ) : (
          <p>Email does not exist.</p>
        )}
        {error && <p>Error: {error}</p>}
      </div>
    </div>
  );
}

export default RenewPassword;
