import React, { useEffect, useState } from 'react';
import { getAuth, onAuthStateChanged, fetchSignInMethodsForEmail, sendPasswordResetEmail } from 'firebase/auth';
import { auth } from '../firebase/Firebase';

import './Styles.css';

function RenewPassword() {
  const [email, setEmail] = useState('');
  const [signInMethods, setSignInMethods] = useState([]);
  const [error, setError] = useState(null);
  const { getAuth, getUserByEmail } = require('firebase/auth');
  const auth = getAuth();
  const checkEmailExists = async () => {
    
      try {
        const userRecord = await getUserByEmail(auth, email);
        
        if (userRecord) {
          // User exists, log the UID
          console.log('User UID:', userRecord.uid);
        } else {
          console.log('User with email does not exist.');
        }
      } catch (error) {
        console.error('Error checking user by email:', error.message);
      }
    
    
    // Example usage
    const userEmail = 'example@example.com';
    checkEmailExists(userEmail);
    }
  return (
  <div>
      <h2>Email Check</h2>
       
    </div>
  );
}

export default RenewPassword;
