import React, { useContext } from 'react';
import { ThemeContext } from '../themeContext/ThemeContext';
import './Style.css';

function Friend() {
  const { amiss } = useContext(ThemeContext);

  return (
    <>
      <div className="App">
        {amiss.map((x, key) => (
          key > 0 && (
            <div className="card bg-dark text-white" key={key}>
              <div>
                <img src={x.photo} alt={`${x.prenom} ${x.nom}`} />
              </div>
              <div>
                <h1>{x.prenom} {x.nom}</h1>
                <button type="button" className="btn btn-success">Lancez la conversation</button>
              </div>
            </div>
          )
        ))}
      </div>
    </>
  );
}

export default Friend;
