import './Style.css'
import  {React } from 'react';
function Conversation() {




  const messages = [
    { text: 'Hello!', sender: 'sender' },
    { text: 'Hi there !', sender: 'recipient' },
    { text: 'How are you?', sender: 'sender' },
    { text: 'ach tma al3chir kaynchi wla hi frchi chno derti m3a dak tota li 3ndha mskouta had dria mskouna 3a9li m3aha tfouta lila m3aha skhonaa , 3ar rbek kari 3wa lyouma ntero ki 3wa m3a ndir formation  bach nweli professional tota point final ?', sender: 'sender' },
    { text: 'ach tma al3chir kaynchi wla hi frchi chno derti m3a dak tota li 3ndha mskouta had dria mskouna 3a9li m3aha tfouta lila m3aha skhonaa , 3ar rbek kari 3wa lyouma ntero ki 3wa m3a ndir formation  bach nweli professional tota point final ?', sender: 'sender' },
    { text: 'ach tma al3chir kaynchi wla hi frchi chno derti m3a dak tota li 3ndha mskouta had dria mskouna 3a9li m3aha tfouta lila m3aha skhonaa , 3ar rbek kari 3wa lyouma ntero ki 3wa m3a ndir formation  bach nweli professional tota point final ach tma al3chir kaynchi wla hi frchi chno derti m3a dak tota li 3ndha mskouta had dria mskouna 3a9li m3aha tfouta lila m3aha skhonaa , 3ar rbek kari 3wa lyouma ntero ki 3wa m3a ndir formation  bach nweli professional tota point final ?', sender: 'recipient' },
  ];
 
  return (
    <>
   <div class="chat-box-container">
 
    <div class="messages" id="messages">
     
    {messages.map((message, index) => (
      <div style={{display:'flex',flexDirection:'row',justifyContent:message.sender === 'sender' ? 'flex-start' : 'flex-end'}}>
        <div key={index} className={message.sender === 'sender' ? 'sender' : 'recipient'}>
          {message.text}
        </div>
        </div>
         
      ))}
    </div>
    
    <div class="input-container d-flex flex-row">
        <input type="text" id="messageInput" class="message-input" placeholder="Type your message..."/>
        <button id="sendButton" class="send-button">Send</button>
    </div>

</div>

    </>
  )
}

export default Conversation
