import React, { useState } from 'react'
import './Style.css'
import { createUserWithEmailAndPassword, signInWithEmailAndPassword } from 'firebase/auth';
import { auth } from '../firebase/Firebase';
import { db } from '../firebase/Firebase';
import { onValue, push, ref, set, update } from "firebase/database";

function Signup() {

  const [nom,setNom]=useState("")
  const [Prenom,setPrenom]=useState("");

  const [Email,setEmail]=useState("");
  const [Password,setPassword]=useState("");
  const [age,setAge]=useState(0);
  const [sex,setSex]=useState("Homme");
 
   
  const show=async()=>{
    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (emailPattern.test(Email) && nom.length>2 && Prenom.length>2 && age > 17 && Password.length >6  ) {
  
      try {
        
        const respone= await createUserWithEmailAndPassword(auth,Email,Password);
        
        if(respone){
    
  
         alert("utilisateur créé"+respone.user.uid)
         
         const newItemRef = push(ref(db, 'users'));
         set(newItemRef, {
          
          age:age,prenom:Prenom,nom:nom,id:respone.user.uid, sex:sex,amis:[{id:'robotid'}],invitation:[{id:"robotid"}],
          
          discussion:[{ id :"robotid" }]
  
  
  
         }
         
         
         
         
         );
  
  
  
  
  
  
  
  
        }
       } catch (error) {
    alert("error")   
     };
  
  
  
  
  
  
  
  
  
  
    }
    
  
  
   
  }
  



  return (
    <>
    <div  class="fory">
    < div class="wrappery">
    <form action="">
      <h1>Register</h1>
      <div class="input-box">
        <input type="text" placeholder="first name" onChange={(e)=>{setPrenom(e.target.value)}}   required/>
 
      </div>
      <div class="input-box">
        <input type="text" placeholder="last name" onChange={(e)=>{setNom(e.target.value)}}  required/>
 
 
      </div>
      <div class="input-box">
        <input type="Email" placeholder=" email"  onChange={(e)=>{setEmail(e.target.value)}} required/>
 
 
      </div>
      
      <div class="input-box">
        <input type="password" placeholder="Password" onChange={(e)=>{setPassword(e.target.value)}}  required/>
   
        </div>
      <div   class="input-box">
       
      <input   

        type="text"
        id="birthday"
        name="birthday"
        placeholder="MM/DD/YYYY"
        onChange={(e)=>{setAge(e.target.value)}}
        
      />
    </div>
    <div>
    <select   class="form-select  input-box"   onChange={(e)=>{setsex(e.target.value)}}>
            
                     <option value="Homme">Homme</option>
                     <option value="femme">Femme</option>
            
            </select>

            </div>
        <button type="submit" class="btn" onClick={()=>show()} >Create Account</button>
         
    </form>
    </div>
    </div>
    </>
  )
}

export default Signup
