import React, { useState } from 'react'
import { sendPasswordResetEmail } from 'firebase/auth';
import { auth } from '../firebase/Firebase';
import './Styles.css'
function RenewPassword() {
    const [email, setEmail] = useState('');
  const [resetSent, setResetSent] = useState(fa);

  const handleResetPassword = async () => {
    try {
      await sendPasswordResetEmail(auth, email);
      
      alert("email");
    } catch (error) {
      console.error('Error sending password reset email', error.message);
    }
  };
  return (
    <>
    
          <div  class="fory">
    < div class="wrappery">
    <form>
      <h2>Forgot Password</h2>
      
      <div class="input-box">
        <input  type="email" value={email} placeholder="enter your email"  onChange={(e) => setEmail(e.target.value)}   required/>
 
      </div>
      <div class="input-box">
      <button  class="btn" onClick={handleResetPassword}>Reset Password</button>
      </div>
       <div class="reset">
      {resetSent && <p>Password reset email sent. Check your email inbox.</p>}
      </div>
      </form>
      </div>
      </div>
      
      
      </>
  )
}

export default RenewPassword
